mapper.select:
SELECT CAR_IDENTIFICATION_NUMBER , CAR_TYPE_CODE , CAR_TYPE , CAR_NAME , CAR_FUEL_CODE , CAR_FUEL , COMPANY , COMPANY_NAME FROM RENTAL_CAR_VIEW WHERE DELIVERY_DATE IS NULL
AND COMPANY = #{company}
AND CAR_TYPE_CODE = #{carTypeCode}

mapper.update:
UPDATE RENTAL_CAR
SET DELIVERY_DATE = SYSDATE
WHERE CAR_IDENTIFICATION_NUMBER = #{car}

mapper.select:
SELECT RN , FN_COMM_NAME(RENTAL_CAR_BRANCH_NO) AS BRANCH , CAR_IDENTIFICATION_NUMBER , CAR_NAME , CAR_TYPE , CAR_FUEL , COMPANY_NAME AS COMPANY , DELIVERY_DATE FROM (SELECT ROW_NUMBER() OVER (ORDER BY RC.DELIVERY_DATE DESC) AS RN, RC.*, RCBC.RENTAL_CAR_BRANCH_NO FROM RENTAL_CAR_VIEW RC, RENTAL_CAR_BRANCHES_CAR RCBC WHERE RCBC.CAR_IDENTIFICATION_NUMBER = RC.CAR_IDENTIFICATION_NUMBER
AND RENTAL_CAR_BRANCH_NO = #{keyword} )
WHERE RN BETWEEN #{firstRecordIndex} AND #{lastRecordIndex}

SELECT COUNT(*)
FROM RENTAL_CAR_BRANCHES_CAR

WHERE RENTAL_CAR_BRANCH_NO = #{keyword}

SELECT NO
      ,CAR_NAME
      ,CAR_TYPE_CODE
      ,CAR_FUEL_CODE
      ,DEFAULT_COST
      ,COMPANY
  FROM NEW_CAR
 WHERE CAR_TYPE_CODE = #{carTypeCode}
   AND CAR_FUEL_CODE = #{carFuelCode}
   AND COMPANY = #{company}

mapper.insert:
INSERT INTO NEW_CAR (
     NO
    ,CAR_NAME
    ,CAR_TYPE_CODE
    ,CAR_FUEL_CODE
    ,DEFAULT_COST
    ,COMPANY
) VALUES (
     SEQ_NEW_CAR_NO.NEXTVAL
    ,#{carName}
    ,#{carTypeCode}
    ,#{carFuelCode}
    ,#{defaultCost}
    ,#{company}
)

mapper.select:
SELECT COUNT(*)
FROM NEW_CAR

    WHERE COMPANY = #{keyword}

mapper.update:
UPDATE NEW_CAR
SET CAR_NAME     = #{carName},
    DEFAULT_COST = #{defaultCost}
WHERE NO = #{no}
