mapper.select:
/* selectBoardList */ SELECT NO , TITLE , CONTENT , HIT , TYPE_CODE , NOTICE_YN , HASH_TAG , START_DATE , END_DATE , REG_ID , REG_DATE , RN FROM ( SELECT NO , TITLE , CONTENT , HIT , TYPE_CODE , NOTICE_YN , HASH_TAG , START_DATE , END_DATE , REG_ID , REG_DATE , ROW_NUMBER() OVER(ORDER BY NO DESC) AS RN FROM BOARD WHERE TYPE_CODE = #{typeCode}
AND TITLE LIKE '%' || #{title} || '%'
)
WHERE RN >= #{startPage} AND RN < #{startPage} + #{pageSize}
ORDER BY RN DESC

WITH Hierarchical_Board AS ( SELECT NO , PARENT_NO , TITLE , CONTENT , HIT , TYPE_CODE , NOTICE_YN , HASH_TAG , START_DATE , END_DATE , REG_ID , REG_DATE , ROW_NUMBER() OVER(ORDER BY NO DESC) AS RN FROM BOARD WHERE TYPE_CODE = #{typeCode} AND REG_ID IN (#{Id}, 'ADMIN')
AND TITLE LIKE '%' || #{title} || '%'
) SELECT LEVEL , NO , PARENT_NO , LPAD(' ', 5*(LEVEL-1)) || CASE WHEN LEVEL > 1 THEN '[답글]: ' ELSE '' END || TITLE AS TITLE , CONTENT , HIT , TYPE_CODE , NOTICE_YN , HASH_TAG , START_DATE , END_DATE , REG_ID , REG_DATE , RN FROM Hierarchical_Board START WITH PARENT_NO = 0 CONNECT BY PRIOR NO = PARENT_NO AND REG_ID IN (#{Id}, 'ADMIN') AND PARENT_NO != 0 ORDER SIBLINGS BY RN DESC

WITH Hierarchical_Board AS ( SELECT NO , PARENT_NO , TITLE , CONTENT , HIT , TYPE_CODE , NOTICE_YN , HASH_TAG , START_DATE , END_DATE , REG_ID , REG_DATE , ROW_NUMBER() OVER(ORDER BY NO DESC) AS RN FROM BOARD WHERE TYPE_CODE = #{typeCode}
AND TITLE LIKE '%' || #{title} || '%'
) SELECT LEVEL , NO , PARENT_NO , LPAD(' ', 5*(LEVEL-1)) || CASE WHEN LEVEL > 1 THEN '[답글]: ' ELSE '' END || TITLE AS TITLE , CONTENT , HIT , TYPE_CODE , NOTICE_YN , HASH_TAG , START_DATE , END_DATE , REG_ID , REG_DATE , RN FROM Hierarchical_Board
WHERE RN >= #{startPage} AND RN < #{startPage} + #{pageSize}
START WITH PARENT_NO = 0 CONNECT BY PRIOR NO = PARENT_NO ORDER SIBLINGS BY RN DESC

		/* selectBoardListSize */
		SELECT COUNT(*)
          FROM BOARD
         WHERE TYPE_CODE = #{typeCode}

           AND TITLE LIKE '%' || #{title} || '%'

mapper.insert:
INSERT INTO BOARD
(
	  NO
	, PARENT_NO
	, TITLE
	, CONTENT
	, HIT
	, TYPE_CODE
	, FILE_NO
	, START_DATE
	, END_DATE
	, REG_ID
	, REG_DATE
)
VALUES
(
	  SEQ_BOARD_NO.NEXTVAL
	, #{parentNo}
	, #{title}
	, #{content}
	, #{hit}
	, #{typeCode}
	, #{fileNo}
	, #{startDate}
	, #{endDate}
	, #{regId}	
	, SYSDATE
)

mapper.update:
UPDATE BOARD
SET 
	  NO         = #{no}
	, TITLE      = #{title}
	, CONTENT    = #{content}
	, HIT        = #{hit}
	, TYPE_CODE  = #{typeCode}
	, FILE_NO    = #{fileNo}
	, START_DATE = #{startDate}
	, END_DATE   = #{endDate}

	, MOD_DATE   = SYSDATE
WHERE NO = #{no}


        UPDATE IMAGE_FILE
        SET
              SAVE_PATH = #{savePath}
            , SAVE_NAME = #{saveName}
            , FILE_NAME = #{fileName}
            , EXTENSION = #{extension}
            , FILE_SIZE = #{fileSize}
            , REG_ID    = #{regId}
	    WHERE FILE_NO   = #{fileNo} 
	      AND SEQ       = #{seq}

mapper.insert:
		INSERT INTO IMAGE_FILE
    	(
