        , #{savePath}
        , #{saveName}
        , #{fileName}
        , #{extension}
        , #{fileSize}
        , #{regId}
        , SYSDATE
    	)

mapper.update:
UPDATE BOARD
SET HIT = HIT + 1
WHERE NO = #{no}
