mapper.select:
/* selectBoardList */ SELECT NO , TITLE , CONTENT , HIT , TYPE_CODE , NOTICE_YN , HASH_TAG , REG_DATE , RN FROM ( SELECT NO , TITLE , CONTENT , HIT , TYPE_CODE , NOTICE_YN , HASH_TAG , TO_CHAR(SYSDATE, 'yyyy-mm-dd') AS REG_DATE , ROW_NUMBER() OVER(ORDER BY NO DESC) AS RN FROM BOARD WHERE TYPE_CODE = #{typeCode}
AND TITLE LIKE '%' || #{title} || '%'
)
WHERE RN >= #{startPage} AND RN < #{startPage} + #{pageSize}
ORDER BY RN DESC

		/* selectBoardListSize */
		SELECT COUNT(*)
          FROM BOARD
         WHERE TYPE_CODE = #{typeCode}

           AND TITLE LIKE '%' || #{title} || '%'

mapper.update:
UPDATE BOARD
SET 
	  TITLE = #{title}
	, CONTENT = #{content}
	, MOD_DATE =#{modDate}
WHERE NO = #{no}
